INSERT INTO `categories` VALUES (1,'Activities',NULL,'<p>Meeting different people and forming new friendships can help you feel good and improve your health and well being. It can also help reduce feelings of loneliness and boredom, allow you to share your experiences and (hopefully) make you smile.&nbsp;</p>\r\n','2013-04-26 16:28:12','2014-09-19 17:43:01',NULL,'activities'),(2,'Reading and creative writing',1,'','2013-04-26 16:32:10','2014-09-19 17:45:46',NULL,'reading-and-creative-writing'),(3,'Arts and crafts',1,NULL,'2013-04-26 16:32:24','2013-05-10 15:51:49',NULL,'arts-and-crafts'),(4,'Drama and music',1,NULL,'2013-04-26 16:32:35','2013-05-10 15:52:20',NULL,'drama-and-music'),(5,'Social clubs',1,NULL,'2013-04-26 16:32:44','2013-05-10 15:52:28',NULL,'social-clubs'),(7,'Health',NULL,'<p>Information about health, weight management and physical activity is available to help you stay healthy and happy.</p>\r\n','2013-04-26 16:33:07','2014-09-21 11:48:16',NULL,'health'),(8,'Health conditions',7,NULL,'2013-04-26 16:33:28','2013-05-10 15:53:07',NULL,'health-conditions'),(9,'Weight management',7,'','2013-04-26 16:33:34','2014-09-19 11:31:51',NULL,'weight-management'),(10,'Fitness and exercise classes',7,NULL,'2013-04-26 16:33:49','2013-05-10 15:53:33',NULL,'fitness-and-exercise-classes'),(11,'Swimming',7,NULL,'2013-04-26 16:34:02','2013-05-10 15:53:47',NULL,'swimming'),(12,'Walking and outdoor activities',7,NULL,'2013-04-26 16:35:10','2013-05-10 15:53:58',NULL,'walking-and-outdoor-activities-1'),(13,'Learning',NULL,'<p>Whether you want to develop a new interest, learn a new skill, keep fit and healthy or simply have fun and make new friends there are classes for you.</p>\r\n\r\n<p>&nbsp;</p>\r\n','2013-04-26 16:35:24','2014-09-21 11:49:07',NULL,'learning'),(14,'Education and learning',13,NULL,'2013-04-26 16:35:39','2013-05-10 15:54:20',NULL,'education-and-learning'),(15,'Support',NULL,'<p>Support can take different forms from emotional to physical assistance or compani